import React,{Component} from 'react';


/* Languages */
import lang_en from '../lang/en/en.json'
import lang_am from '../lang/am/am.json'


const layout = (ChildComponent,type = 'default') => 
    class Layout extends Component {

        static get propTypes() { 
            return { 
                children: PropTypes.any, 
                onClickOut: PropTypes.func 
            }; 
        }


        render (){
            console.log(this.props.language)
            const {...rest} = this.props
            const {language} = this.props
            const lang = language ? language?.language === 'EN' ? lang_en : lang_am : lang_en
            const combineRest = {...rest,lang}

            return (
                <>
                {/* for default pages*/}
                {
                    (type === 'default') ?
                    <>
                        <span>header</span>
                            <ChildComponent
                               {...combineRest}
                            />
                            <span>footer</span>
                    </> : null
                }
                {/* for 404 page*/}
                {
                    (type === 'not-found') ?
                        <>
                            <span>header</span>
                            <ChildComponent
                                {...combineRest}
                            />
                            <span>footer</span>
                        </> : null
                }
            </>
            )
        }
    }

    export default layout
