                    </> : null
                }
                {/* for 404 page*/}
                {
                    (type === 'not-found') ?
                        <>
                            <span>header</span>
                            <ChildComponent
                                {...combineRest}
                            />
                            <span>footer</span>
                        </> : null
                }
            </>
            )
        }
    }

    export default layout
