import React,{Component} from 'react';

/* Languages */
import lang_en from '../lang/en/en.json'
import lang_am from '../lang/am/am.json'


const layout = (ChildComponent,type = 'default') => 
function (props){
    const {...rest} = props
    console.log(rest)
    const {language} = props
    const lang = language ? language?.language === 'EN' ? lang_en : lang_am : lang_en
    const combineRest = {...rest,lang}

    return (
        <>
        {/* for default pages*/}
        {
            (type === 'default') ?
            <>
                <span>header</span>
                    <ChildComponent
                       {...combineRest}
                    />
                    <span>footer</span>
            </> : null
        }
        {/* for 404 page*/}
        {
            (type === 'not-found') ?
                <>
                    <span>header</span>
                    <ChildComponent
                        {...combineRest}
                    />
                    <span>footer</span>
                </> : null
        }
    </>
    )
}


    export default layout
